import React, { useEffect, useState } from 'react'
import Head from './Head'
import Footer from '../Home/Footer'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'
import Grid from '@material-ui/core/Grid'
import IsoTopeGrid from "react-isotope";


import './plugins/font-awesome-4.7.0/css/font-awesome.min.css'
import './plugins/OwlCarousel2-2.2.1/owl.carousel.css'
import './plugins/OwlCarousel2-2.2.1/owl.theme.default.css'
import './plugins/OwlCarousel2-2.2.1/animate.css'
import './styles/main_styles.css'
import './styles/responsive.css'




const CAT_QUERY = gql`
{
    elect_categorias {
      categoria
    }
}
`
const PROD_QUERY = gql`
query Productos ($categoria: String)
{
    electricos(categoria: $categoria)
    {
  	    id_elec
        nombre
        categoria
        img
    }  
}
`







export default function Electricos() {
    return (
        <div>
            <Head />
            <Body />
            <Footer />
        </div>
    )
}


function Body() {
    const [filters, setFilters] = useState([])
    // useEffect(() => {
    //     const script = document.createElement("script");

    //     script.src = "./plugins/Isotope/isotope.pkgd.min.js";
    //     script.async = true;

    //     document.body.appendChild(script);

    // }, [])


    return (
        <div className="new_arrivals">
            <div className="container">
                <div className="row">
                    <div className="col text-center">
                        <div className="section_title new_arrivals_title">
                            <h2>Productos Eléctricos</h2>
                        </div>
                    </div>
                </div>
                <div className="row align-items-center">
                    <div className="col text-center">
                        <div className="new_arrivals_sorting">
                            <ul className="arrivals_grid_sorting clearfix button-group filters-button-group">
                                <li className="grid_sorting_button button d-flex flex-column justify-content-center align-items-center active is-checked">
                                    TODOS
                                </li>
                                <Query query={CAT_QUERY}>
                                    {
                                        ({ loading, error, data }) => {
                                            if (loading) return <p>Loading..</p>
                                            if (error) console.log(error)
                                            return data.elect_categorias.map((cat, i) => {
                                                return (
                                                    <li key={i} className="grid_sorting_button button d-flex flex-column justify-content-center align-items-center">
                                                        {cat.categoria}
                                                    </li>
                                                )
                                            })

                                        }
                                    }
                                </Query>
                            </ul>
                        </div>
                    </div>
                </div>
                <div className="row">
                    <div className="col" style={{ marginTop: '50px', marginBottom: '50px' }}>
                        <Grid container spacing={1} alignItems="center">

                            <Query query={PROD_QUERY} variables={{ categoria: 'all' }}>
                                {
                                    ({ loading, error, data }) => {
                                        if (loading) return <p>Loading..</p>
                                        if (error) console.log(error)
                                        return data.electricos.map(elect => (
                                            <Grid item xs={0} key={elect.id_elec} style={{ background: '#e8e6e6' }}>
                                                <center>
                                                    <Card prod={elect} />
                                                </center>

                                            </Grid>
                                        ))
                                    }
                                }
                            </Query>
                        </Grid>
                    </div>
                </div>
            </div>

        </div>
    )
}


function Card({ prod, addToCot }) {
    return (

        <div className="flip-card">
            <div className="flip-card-inner">
                <div className="flip-card-front">
                    <img className="img-prod" src={prod.img} alt="Avatar" />
                </div>
                <div className="flip-card-back d-flex flex-column justify-content-around">
                    <h3>{prod.nombre}</h3>
                    <h5>{prod.categoria}</h5>
                    <center>
                        <button
                            className="btn"
                            style={{ width: ' 150px', background: '#fff', color: '#c2000a' }}
                            onClick={
                                e => {
                                    e.preventDefault()
                                    //addToCot(body => [...body, ` ${prod.nombre} - ${prod.categoria}`])
                                }
                            }
                        >
                            Pedir cotización
                        </button>
                    </center>

                </div>
            </div>
        </div>

    )

}
