import React, { useEffect, useState } from 'react'
import Head from './Head'
import Footer from '../Home/Footer'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'
import IsoTopeGrid from "react-isotope";



import './plugins/font-awesome-4.7.0/css/font-awesome.min.css'
import './plugins/OwlCarousel2-2.2.1/owl.carousel.css'
import './plugins/OwlCarousel2-2.2.1/owl.theme.default.css'
import './plugins/OwlCarousel2-2.2.1/animate.css'
import './styles/main_styles.css'
import './styles/responsive.css'


import testimg from './images/test.png'
import prodtwo from './images/product_2.png'
import prodthree from './images/product_3.png'
import prodfour from './images/product_4.png'
import prodfive from './images/product_5.png'
import prodsix from './images/product_6.png'
import prodseven from './images/product_7.png'
import prodeight from './images/product_8.png'
import prodnine from './images/product_9.png'
import prodten from './images/product_10.png'

const PROD_QUERY = gql`
query Productos ($categoria: String)
{
    electricos(categoria: $categoria)
    {
  	    id_elec
        nombre
        categoria
        img
    }  
}`

const CAT_QUERY = gql`
{
    elect_categorias {
      categoria
    }
}`


export default function Electricos() {
    return (
        <div>
            <Head />
            <Body />
            <Footer />
        </div>
    )
}


function Body() {
    const [filters, setFilters] = useState(['all'])
    // useEffect(() => {
    //     const script = document.createElement("script");

    //     script.src = "./plugins/Isotope/isotope.pkgd.min.js";
    //     script.async = true;

    //     document.body.appendChild(script);

    // }, [])


    return (
        <div className="new_arrivals">
            <div className="container">
                <div className="row">
                    <div className="col text-center">
                        <div className="section_title new_arrivals_title">
                            <h2>New Arrivals</h2>
                        </div>
                    </div>
                </div>
                <div className="row align-items-center">
                    <div className="col text-center">
                        <div className="new_arrivals_sorting">
                            <ul className="arrivals_grid_sorting clearfix button-group filters-button-group">
                                <li className="grid_sorting_button button d-flex flex-column justify-content-center align-items-center active is-checked">
                                    TODOS
                                </li>
                                <Query query={CAT_QUERY}>
                                    {
                                        ({ loading, error, data }) => {
                                            if (loading) return <p>Loading..</p>
                                            if (error) console.log(error)
                                            return data.elect_categorias.map((cat, i) => {
                                                setFilters(filters => [...filters, cat.categoria])
                                                return (
                                                    <li key={i} className="grid_sorting_button button d-flex flex-column justify-content-center align-items-center">
                                                        {cat.categoria}
                                                    </li>
                                                )
                                            })

                                        }
                                    }
                                </Query>
                            </ul>
                        </div>
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <IsoTopeGrid
                            noOfCols={3}
                            filters={filters}
                        >
                            <Query query={PROD_QUERY} variables={{ categoria: 'all' }}>
                                {
                                    ({ loading, error, data }) => {
                                        if (loading) return <p>Loading..</p>
                                        if (error) console.log(error)
                                        return data.electricos.map(elect => (
                                            // <Grid item xs={4} key={elect.id_elec} style={{ background: '#e8e6e6' }}>
                                            //     <center>
                                            //         <Card prod={elect} addToCot={setBody} />
                                            //     </center>

                                            // </Grid>
                                            <TestCard key={elect.id_elec} className={elect.categoria} />
                                        ))
                                    }
                                }
                            </Query>
                        </IsoTopeGrid>

                    </div>
                </div>
            </div>

        </div>
    )
}


function TestCard() {

    return (
        <div className="product-item men">
            <div className="product discount product_filter">
                <div className="product_image">
                    <img src={testimg} alt="" />
                </div>
                <div className="favorite favorite_left"></div>
                <div
                    className="product_bubble product_bubble_right product_bubble_red d-flex flex-column align-items-center">
                    <span>-$20</span></div>
                <div className="product_info">
                    <h6 className="product_name"><a href="single.html">Fujifilm X100T 16 MP Digital Camera
                    (Silver)</a></h6>
                    <div className="product_price">$520.00<span>$590.00</span></div>
                </div>
            </div>
            <div className="red_button add_to_cart_button"><a href="#">add to cart</a></div>
        </div>

    )
}


