import React from 'react'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'

const CAT_QUERY = gql`
{
    s_categorias {
        categorias
    }
}
`
export default function Home() {
    return (
        <div className="container-fluid bg-info">
            <div className="row">
                <div className="col-3 bg-danger">
                    <h4><b>CATEGORIAS</b></h4>
                    <hr style={{ float: 'left', width: '150px' }}></hr>
                    <Query query={CAT_QUERY}>
                        {
                            ({ loading, error, data }) => {
                                if (loading) return <p>Loading..</p>
                                if (error) console.error(error)
                                return data.s_categorias.map(cat, i => (
                                    <p key={i}>{cat}</p>
                                ))
                            }
                        }
                    </Query>


                </div>
                <div className="col-7 bg-dark">

                </div>
            </div>
        </div>
    )
}
