import React, { createContext, useRef } from 'react'
import { useHistory } from 'react-router-dom'

export const refProv = createContext()

export default function RefProvider(props) {
    const history = useHistory()
    const aboutRef = useRef(null)
    const featRef = useRef(null)
    const handRef = useRef(null)
    const contactRef = useRef('notnull')

    const testScroll = (ref) => {
        history.push('/')
        setTimeout(afterlink(ref), 2000)
    }

    const afterlink = (ref) => {
        console.log(ref.current.offsetTop)
    }

    return (
        <refProv.Provider value={{ aboutRef, featRef, handRef, contactRef, testScroll }}>
            {props.children}
        </refProv.Provider>
    )
}
