import React from 'react'
import './Product.css'
import { makeStyles } from '@material-ui/core/styles'
import Paper from '@material-ui/core/Paper'
import Grid from '@material-ui/core/Grid'
import HeaderTop from './HeaderTop'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}))

const CAT_QUERY = gql`
{
    e_categorias {
      categoria
    }
}
`
const PROD_QUERY = gql`
{
    p_electronicos {
        id_electronico
        categoria
        nombre
        img
    }
}
`

export default function Product() {
    const classes = useStyles()


    return (
        <div className="container-fluid">
            <div className="row" style={{ height: '160px' }}>
                <div className="col fixed-top">
                    <HeaderTop />
                </div>
            </div>
            <div className="row">
                <div className="col-3">
                    <h5 className="cat-hd mg-l"><b>CATEGORÍAS</b></h5>
                    <hr className="mg-l" />
                    <Query query={CAT_QUERY}>
                        {
                            ({ loading, error, data }) => {
                                if (loading) return <p>Loading..</p>
                                if (error) console.log(error)
                                return data.e_categorias.map((cat, i) => (
                                    <p key={i} className="mg-l grey-color cat-items">{cat.categoria}</p>
                                ))
                            }
                        }
                    </Query>
                    <hr className="mg-l" />
                    <Cotizacion />
                </div>

                <div className="col">
                    <center><h5 className="cat-hd"><b>PRODUCTOS ELECTRÓNICOS</b></h5></center>
                    <hr />
                    <div className="container" style={{ background: '#e8e6e6' }}>
                        <div className={classes.root}>
                            <Grid container spacing={4} alignItems="center">

                                <Query query={PROD_QUERY}>
                                    {
                                        ({ loading, error, data }) => {
                                            if (loading) return <p>Loading..</p>
                                            if (error) console.log(error)
                                            return data.p_electronicos.map(elect => (
                                                <Grid item xs={4} key={elect.id_electronico}>
                                                    <Card prod={elect} />
                                                </Grid>
                                            ))
                                        }
                                    }
                                </Query>
                            </Grid>
                        </div>

                    </div >
                </div>
            </div>
        </div>


    )
}


function Card({ prod }) {
    return (

        <div className="flip-card">
            <div className="flip-card-inner">
                <div className="flip-card-front">
                    <img className="img-prod" src={prod.img} alt="Avatar" />
                </div>
                <div className="flip-card-back d-flex flex-column justify-content-around">
                    <h3>{prod.nombre}</h3>
                    <h5>{prod.categoria}</h5>
                    <center><button className="btn" style={{ width: ' 150px', background: '#fff', color: '#c2000a' }}>Pedir cotización</button></center>

                </div>
            </div>
        </div>

    )

}


function Cotizacion() {
    return (
        <form className="d-flex flex-column main-form">
            <center><h5>¡Haz tu pedido!</h5></center>
            <input type="text" value={name} placeholder="Nombre:" required onChange={e => setName(e.target.value)} />
            <input type="email" value={email} placeholder="Email:" required onChange={e => setEmail(e.target.value)} />
            <input type="tel" value={tel} placeholder="Teléfono:" pattern="[0-9]{3} [0-9]{3} [0-9]{3}" required onChange={e => setTel(e.target.value)} />
            <input type="text" value={emp} placeholder="Empresa:" required onChange={e => setEmp(e.target.value)} />
            <textarea rows="4" value={body} placeholder="Haga su consulta" required onChange={e => setBody(e.target.value)} />
            <button type="submit" className="btn send-button">Enviar</button>
        </form>
    )
}
