import React from 'react'
import './Chat.css'


export default function Chat() {

    const data = [
        {
            socket_id: 'sock-1',
            name: 'Franco',
            messages: [
                { from: 0, msg: 'Bienvenido al chat de maxpower Franco' },
                { from: 1, msg: 'Bienvenido al chat de maxpower Franco' },
                { from: 0, msg: 'test Franco' }
            ]
        },
        {
            socket_id: 'sock-2',
            name: 'Tomas',
            messages: [
                { from: 0, msg: 'Bienvenido al chat de maxpower Tomas' },
                { from: 1, msg: 'Hola server' },
                { from: 1, msg: 'pedido de cotizacion' }
            ]
        },
        {
            socket_id: 'sock-3',
            name: 'Leonel',
            messages: [
                { from: 0, msg: 'Bienvenido al chat de maxpower Leonel' },
                { from: 1, msg: 'Hola maxpa' },
                { from: 1, msg: 'como va?' }
            ]
        },
    ]



    return (
        <div className="container main-chat">
            <div className="row h-100">
                <div className="col-3 h-100">

                    <div className="row toprow">
                        <h4 className="mx-auto my-auto">Maxpower Chat</h4>
                    </div>

                    <div className="row mainrow d-flex flex-column">
                        {
                            data.map(chat => (
                                <ChatClient
                                    key={chat.socket_id}
                                    ids={chat.socket_id}
                                    name={chat.name}
                                    lastmsg={chat.messages[chat.messages.length - 1]}
                                />
                            ))
                        }
                    </div>
                </div>

                <div className="col h-100" style={{ borderBottom: '1px solid black', borderTop: '1px solid black', borderRight: '1px solid black' }}>

                </div>



            </div>
        </div>
    )
}


function ChatClient({ name, ids, lastmsg }) {

    return (
        <div id={ids} className="d-flex flex-column w-100" style={{ height: '60px', borderBottom: '1px solid black' }}>
            <span className="mx-auto" style={{ fontSize: '20px' }}>{name}</span>
            <span className="mx-auto" style={{ fontSize: '12px' }}>{lastmsg.msg}</span>
        </div>
    )
}

function ClientContent() {
    return (
        <div className="row h-100">
            <div className="w-100 toprow" align="center">
                <h4 className="mx-auto my-auto">Test</h4>
            </div>

            <div className="w-100 mainrow">
                <div className="content d-flex flex-column overflow-auto">
                    {/* MESSAGES HERE */}
                </div>
                <div className="send-div">
                    <div className="in-msg d-flex justify-content-around">
                        <input type="text" />
                        <span className="text-primary">Send</span>
                    </div>
                </div>
            </div>



        </div>

    )
}
