import React from 'react'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'

const CAT_QUERY = gql`
{
    s_categorias{
      categoria
    }
  }
`
const PROD_QUERY = gql`
{
    p_seguridad{
      id_seg
      nombre
      img
    }
}
`
export default function Home() {
    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-3">
                    <h4><b>CATEGORIAS</b></h4>
                    <hr style={{ float: 'left', width: '150px' }}></hr>
                    <br />
                    <br></br>
                    <Query query={CAT_QUERY}>
                        {
                            ({ loading, error, data }) => {
                                if (loading) return <p>Loading..</p>
                                if (error) console.error(error)
                                console.log(data)
                                return data.s_categorias.map((cat, i) => (
                                    <p key={i}>{cat.categoria}</p>
                                ))
                            }
                        }
                    </Query>
                    <h4><b>MARCAS</b></h4>
                    <hr style={{ float: 'left', width: '100px' }}></hr>
                    <br />
                    <br></br>

                    <Query query={CAT_QUERY}>
                        {
                            ({ loading, error, data }) => {
                                if (loading) return <p>Loading..</p>
                                if (error) console.error(error)
                                console.log(data)
                                return data.s_categorias.map((cat, i) => (
                                    <p key={i}>{cat.categoria}</p>
                                ))
                            }
                        }
                    </Query>


                </div>
                <div className="col-7 bg-dark">
                    <Query query={PROD_QUERY}>
                        {
                            ({ loading, error, data }) => {
                                if (loading) return <p>Loading..</p>
                                if (error) console.error(error)
                                console.log(data)
                                return data.p_seguridad.map((prod, i) => (
                                    <div key={prod.id_seg} className="card" style={{ width: '300px', margin: '20px' }}>
                                        <img src={prod.img} className="card-img-top" alt="..." />
                                        <h5 className="card-title">{prod.nombre}</h5>
                                        <a href="#" className="btn btn-primary">Ir al producto</a>
                                    </div>
                                ))
                            }
                        }
                    </Query>

                </div>
            </div>
        </div>
    )
}
