import React from 'react'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'

const CAT_QUERY = gql`
{
    s_categorias{
      categoria
    }
  }
`
export default function Home() {
    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-3">
                    <h4><b>CATEGORIAS</b></h4>
                    <hr style={{ float: 'left', width: '150px' }}></hr>
                    <br />
                    <br></br>
                    <Query query={CAT_QUERY}>
                        {
                            ({ loading, error, data }) => {
                                if (loading) return <p>Loading..</p>
                                if (error) console.error(error)
                                console.log(data)
                                return data.s_categorias.map((cat, i) => (
                                    <p key={i}>{cat.categoria}</p>
                                ))
                            }
                        }
                    </Query>


                </div>
                <div className="col-7 bg-dark">
                    <div className="card" style={{ width: '18rem' }}>
                        <img src="..." className="card-img-top" alt="..." />
                        <h5 className="card-title">Card title</h5>
                        <a href="#" class="btn btn-primary">Ir al producto</a>
                    </div>
                </div>
            </div>
        </div>
    )
}
